

new_string = ""
new_string = "123456789"
new_string = new_string.replace("\n", "")
new_string = new_string.upper()
forward_lst = [new_string[i:i + 3] for i in range(0, len(new_string), 3)]
backward_lst = [new_string[i:i + 3] for i in range(len(new_string)-3, -3, -3)]
print(forward_lst)
forward_lst.reverse()
print(forward_lst)
print(backward_lst)